package study4;

public class class3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// ceil() : 올림메소드(소숫점아래)
		System.out.println( Math.ceil(98.2)); //99.0
		System.out.println( Math.ceil(98.7)); //99.0
		
		//floor() : 내림메소드(소숫점아래)
		System.out.println( Math.floor(98.2));
		System.out.println( Math.floor(98.7));
		
		//round() : 반올림메소드(정수형)
		System.out.println( Math.round(98.2));
		System.out.println( Math.round(98.7));
		
		//rint() : 반올림메소드(실수형)
				System.out.println( Math.rint(98.2));
				System.out.println( Math.rint(98.7));
				
		//abs() : 반올림메소드(음수만 적용)
		System.out.println( Math.abs(-98));
		System.out.println( Math.abs(98));
		
		//pow() : 제곱수
				System.out.println( Math.pow(10,2));
				System.out.println( Math.pow(10,3));
		
		//sqrt() : 제곱근 반환(정수형)
				System.out.println( Math.sqrt(100));
				System.out.println( Math.sqrt(25));
				
		//max() / min{}
				// : 반올림메소드(정수형)
				System.out.println( Math.round(98.2));
				System.out.println( Math.round(98.2));
	}

}
